import React, { useState } from 'react';

const Projects = () => {
  const [activeCategory, setActiveCategory] = useState('all');

  const projects = [
    {
      id: 1,
      title: 'Brand Identity Design',
      image: 'https://images.unsplash.com/photo-1600776960967-528e3a3e35b7?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      category: 'branding'
    },
    {
      id: 2,
      title: 'Web Design Project',
      image: 'https://images.unsplash.com/photo-1522542550221-31fd19575a2d?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      category: 'web'
    },
    {
      id: 3,
      title: 'App UI Design',
      image: 'https://images.unsplash.com/photo-1507238691740-187a5b1d37b8?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      category: 'ui'
    },
    {
      id: 4,
      title: 'Print Design',
      image: 'https://images.unsplash.com/photo-1586717799252-bd134ad00e26?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      category: 'print'
    },
    {
      id: 5,
      title: 'Social Media Design',
      image: 'https://images.unsplash.com/photo-1563986768494-4dee2763ff3f?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      category: 'social'
    },
    {
      id: 6,
      title: 'Typography Project',
      image: 'https://images.unsplash.com/photo-1588345921523-c2dcdb7f1dcd?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      category: 'print'
    }
  ];

  const filteredProjects = activeCategory === 'all' 
    ? projects 
    : projects.filter(project => project.category === activeCategory);

  return (
    <section id="projects" className="py-20 bg-black text-white">
      <div className="container mx-auto px-4">
        <div className="text-center mb-12">
          <h2 className="text-3xl font-bold mb-8">PORTFOLIO</h2>
        </div>
        
        <div className="flex flex-wrap justify-center mb-12">
          <button 
            onClick={() => setActiveCategory('all')}
            className={`mx-4 px-2 py-1 border-b-2 transition-colors mb-2 ${activeCategory === 'all' ? 'border-white text-white' : 'border-transparent text-gray-400 hover:text-white'}`}
          >
            All
          </button>
          <button 
            onClick={() => setActiveCategory('branding')}
            className={`mx-4 px-2 py-1 border-b-2 transition-colors mb-2 ${activeCategory === 'branding' ? 'border-white text-white' : 'border-transparent text-gray-400 hover:text-white'}`}
          >
            Branding
          </button>
          <button 
            onClick={() => setActiveCategory('web')}
            className={`mx-4 px-2 py-1 border-b-2 transition-colors mb-2 ${activeCategory === 'web' ? 'border-white text-white' : 'border-transparent text-gray-400 hover:text-white'}`}
          >
            Web
          </button>
          <button 
            onClick={() => setActiveCategory('ui')}
            className={`mx-4 px-2 py-1 border-b-2 transition-colors mb-2 ${activeCategory === 'ui' ? 'border-white text-white' : 'border-transparent text-gray-400 hover:text-white'}`}
          >
            UI/UX
          </button>
          <button 
            onClick={() => setActiveCategory('print')}
            className={`mx-4 px-2 py-1 border-b-2 transition-colors mb-2 ${activeCategory === 'print' ? 'border-white text-white' : 'border-transparent text-gray-400 hover:text-white'}`}
          >
            Print
          </button>
          <button 
            onClick={() => setActiveCategory('social')}
            className={`mx-4 px-2 py-1 border-b-2 transition-colors mb-2 ${activeCategory === 'social' ? 'border-white text-white' : 'border-transparent text-gray-400 hover:text-white'}`}
          >
            Social
          </button>
        </div>
        
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-5">
          {filteredProjects.map(project => (
            <div key={project.id} className="overflow-hidden group relative">
              <img 
                src={project.image} 
                alt={project.title} 
                className="w-full h-64 object-cover transition-transform duration-500 group-hover:scale-110"
              />
              <div className="absolute inset-0 bg-black bg-opacity-70 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                <div className="text-center">
                  <h3 className="text-xl font-semibold">{project.title}</h3>
                  <a href="#" className="inline-block mt-4 border-b border-white text-sm hover:text-gray-300">View Project</a>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Projects;
